mapper.select:
select id, productName, imagePath, cost, companyName, detailDescription, eventName, mileage, categoryId, created, updated
from product
where id=#{id}

select id, productName, imagePath, cost, companyName, detailDescription, eventName, mileage, categoryId, created, updated
from product
where categoryId = #{id}

mapper.update:
update product set productName=#{productName}, imagePath=#{imagePath}, cost=#{cost}, companyName=#{companyName},
detailDescription=#{detailDescription}, eventName=#{eventName}, mileage=#{mileage}, categoryId=#{categoryId}
where id=#{id}
